import React from "react";
import propTypes from "prop-types";
import { useSelector } from "react-redux";
import { Card, Button, Popover, Avatar } from "antd";
import { EllipsisOutlined, MessageOutlined, HeartOutlined, RetweetOutlined } from "@ant-design/icons";

import PostImages from './PostImages';

const PostCard = ({ post }) => {
    const { me } = useSelector
    const id = me?.id;  //옵셔널 체이닝

    return (
        <div>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images}/>}
                actions={[                  //jsx에서 배열 안에 값 넣을 때는 key 필수
                    <RetweetOutlined key="rwtweet"/>,
                    <HeartOutlined key="heart"/>,
                    <MessageOutlined key="comment"/>,
                    <Popover key="more" content={(
                        <Button.Group>
                            {id && post.User.id === id ? (
                                <>
                                    <Button type="primary">수정</Button>
                                    <Button type="danger">삭제</Button>
                                </>
                            ) : <Button>신고</Button>}
                        </Button.Group>
                    )}>
                        <EllipsisOutlined />
                    </ Popover>
                ]}
            >
                <Card.Meta
                    avatar={<Avatar>{post.User.nickname[0]}</Avatar>}
                    title={post.User.nickname}
                    description={post.content}
                />
            </Card>
            {/* <CommentForm/>
            <Comments /> */}
        </div>
    );
};

PostCard.propTypes = {
    post: propTypes.shape({     //shape으로 더 자세하게 정해줄 수 있음. 하위의 object도 shape으로 또 더 자세히 써줄 수 있음.
        id: propTypes.number,
        User: propTypes.object,
        content: propTypes.string,
        createdAt: propTypes.object,
        Comments: propTypes.arrayOf(propTypes.object),
        Images: propTypes.arrayOf(propTypes.object),
    }).isRequired,
}

export default PostCard;
